//适配移动端将px 转化为rem;
(function (doc, win) {
    var docEl = doc.documentElement,
        resizeEvt = 'orientationchange' in window ? 'orientationchange' : 'resize',
        recalc = function () {
            var clientWidth = docEl.clientWidth;
            if (!clientWidth) return;
            if(clientWidth>=750){
                docEl.style.fontSize = '100px';
            }else{
                docEl.style.fontSize = 100 * (clientWidth / 750) + 'px';
            }
        };
    if (!doc.addEventListener) return;
    win.addEventListener(resizeEvt, recalc, false);
    doc.addEventListener('DOMContentLoaded', recalc, false);
})(document, window);


function my_alert(type, text,inte,btn_text,obj){
				var data = {
			        type:type,
			        text:text,
			        inte:inte,
			        desc:"",
			        btn_text:btn_text
			    };
			    var alertBox = alertFn(data);
			    $('#alert_box').remove();
			    $('body').append(alertBox);
			
			    alertBox.show(); //在固定的位置直接使用
			    alertBox.find('#alert_confirm').on('click',obj);
			    alertBox.find('#alert_confirm').on('click',function(){
			    	alertBox.hide();
			    });
}

var alertFn = function(o){
    var o = o || {};
    var centerCon = ''; //中心内容
    var centerBtn = ''; //按钮选择
    var alertAll = '';
    if(o.type&&o.type=='confirm_correct'){
        centerCon = '<div style="width:1.46rem;height:1.46rem;margin:0.54rem auto 0.36rem;"><img style="width:100%;height:100%;" src="data:image/png;base64,"></div>'+'<div style="font-size:0.4rem;color:#666;margin-bottom:0.5rem;">'+o.text+
                    '</div>'+'<div style="font-size:0.32rem;color:#666;margin-bottom:0.5rem;">'+o.inte+'</div>'+
                    '<div style="font-size:12px;color:#707070;padding-bottom:10px;width:80%;margin-left:10%;word-break:break-all">'+o.desc+'</div>';
        centerBtn = '<div style="display:table;width:100%;height:1.14rem;table-layout:fixed;border-top:solid 1px #e1e1e1;">'+
                    '    <div style="display:table-cell;vertical-align:middle;text-align:center;font-size:0.34rem;height:1.14rem;border-left:solid 1px #e4e4e4;color:#333;" id="alert_confirm">'+o.btn_text[0]+'</div>'+
                    '</div>';
    }else if(o.type&&o.type=='confirm_error'){
        centerCon = '<div style="width:1.46rem;height:1.46rem;margin:0.54rem auto 0.36rem;"><img style="width:100%;height:100%;" src="data:image/png;base64,"></div>'+'<div style="font-size:0.34rem;line-height:0.5rem;color:#666;margin:0 0.2rem 0.5rem 0.2rem;">'+o.text+
                    '</div>'+'<div style="font-size:0.32rem;color:#666;margin-bottom:0.5rem;">'+o.inte+'</div>'+
                    '<div style="font-size:12px;color:#707070;padding-bottom:10px;width:80%;margin-left:10%;word-break:break-all">'+o.desc+'</div>';
        centerBtn = '<div style="display:table;width:100%;height:1.14rem;table-layout:fixed;border-top:solid 1px #e1e1e1;">'+
                    '    <div style="display:table-cell;vertical-align:middle;text-align:center;font-size:0.34rem;height:1.14rem;border-left:solid 1px #e4e4e4;color:#333;" id="alert_confirm">'+o.btn_text[0]+'</div>'+
                    '</div>';
    }else if(o.type&&o.type=='select'){
        centerCon = '<div style="width:1.46rem;height:1.46rem;margin:0.54rem auto 0.36rem;"><img style="width:100%;height:100%;" src="data:image/png;base64,"></div>'+'<div style="font-size:0.34rem;line-height:0.4rem; color:#666;margin-bottom:0.5rem;">'+o.text+
                    '</div>'+'<div style="font-size:0.32rem;color:#666;margin-bottom:0.5rem;">'+o.inte+'</div>'+
                    '<div style="font-size:12px;color:#707070;padding-bottom:10px;width:80%;margin-left:10%;word-break:break-all">'+o.desc+'</div>';
        
        centerBtn = '<div style="display:table;width:100%;height:1.14rem;table-layout:fixed;border-top:solid 1px #e1e1e1;">'+
                    '    <div style="display:table-cell;vertical-align:middle;text-align:center;font-size:0.34rem;height:1.14rem;color:#333;" id="alert_cancel">'+o.btn_text[0]+'</div>'+
                    '    <div style="display:table-cell;vertical-align:middle; color:red; text-align:center;font-size:0.34rem;height:1.14rem;border-left:solid 1px #e1e1e1;color:#333;" id="alert_confirm">'+o.btn_text[1] +'</div>'+
                    '</div>';
    }else if(o.type&&o.type=='abnormal'){
        centerCon = '<div style="width:1.46rem;height:1.46rem;margin:0.54rem auto 0.36rem;"><img style="width:100%;height:100%;" src="data:image/png;base64,"></div>'+'<div style="font-size:0.4rem;font-weight:600; color:#666;margin-bottom:0.5rem;">'+o.text+
                    '</div>'+'<div style="font-size:0.32rem;color:#666;margin-bottom:0.5rem;">'+o.inte+'</div>'+
                    '<div style="font-size:12px;color:#707070;padding-bottom:10px;width:80%;margin-left:10%;word-break:break-all">'+o.desc+'</div>';
        centerBtn = '<div style="display:table;width:100%;height:1.14rem;table-layout:fixed;border-top:solid 1px #e1e1e1;">'+
                    '    <div style="display:table-cell;vertical-align:middle;text-align:center;font-size:0.34rem;height:1.14rem;border-left:solid 1px #e4e4e4;color:#333;" id="alert_confirm">'+o.btn_text[0]+'</div>'+
                    '</div>';
    };
    alertAll  = $('<div style="display:none;overflow:hidden;" id="alert_box">'+
                '    <div style="position:fixed;left:0;top:0;right:0;bottom:0;background:rgba(0,0,0,.1);z-index:10000;" id="alert_bg"></div>'+
                '    <div style="width:6rem; position:fixed;top:50%;left:50%;z-index:10001;-webkit-transform:translate(-50%,-50%);-moz-transform:translate(-50%,-50%);-o-transform:translate(-50%,-50%);transform:translate(-50%,-50%);">'+
                '        <div style="overflow:hidden;background:#fff;border-radius:10px;">'+
                '            <div style="width:100%;text-align:center;">'+centerCon+'</div>'+centerBtn+
                '        </div>'+
                '    </div>'+
                '</div>');
    alertAll.find('#alert_cancel').on('click',function(){
        alertAll.hide();
        return false;
    });
    return alertAll;
};