// 截取链接地址字符js
function urlGet() {
    var aQuery = window.location.href.split("?"); //取得Get参数
    var aGET = new Array();
    if (aQuery.length > 1) {
        var aBuf = aQuery[1].split("&");
        for (var i = 0, iLoop = aBuf.length; i < iLoop; i++) {
            var aTmp = aBuf[i].split("="); //分离key与Value
            aGET[aTmp[0]] = aTmp[1];
        }
    }
    return aGET;
}

// 横屏友好提示js
$(window).bind('orientationchange', function(e) {
    orient();
});
orient();
function orient() {
    $('#mask_box').remove();
    var mask=$('<div id="mask_box" style="width:100%;height: 100%;position: absolute;left: 0;top: 0;z-index: 999;display:none;background:white;line-height:100%;text-align:center;color:#234567;font-size:0.4rem;"><img style="width:3rem;height:2rem;margin-bottom:0.2rem;" src="data:image/jpg;base64,"><br>为了更好的视觉效果,请竖屏查看！</div>');
     $('body').append(mask);        
    if (window.orientation == 0 || window.orientation == 180) {
        $('#mask_box').css("display","none");
        return false;
    } else if (window.orientation == 90 || window.orientation == -90) {
        $('#mask_box').css("display","block");
        return false;
    } 
    
}

